}].map(item => {
    return (

        <li key={props.meal.id}>
            <h5 className={styles.mealName}>{props.meal.name}</h5>
            <p className={styles.mealDesc}>{props.meal.description}</p>
            <h6 className={styles.mealPrice}>${props.meal.price.toFixed(2)}</h6>
        </li>

    );
})

const Cart = () => {
    return (
        <div className={styles.cartContainer}>
            <div className={styles.cartItemsContainer}></div>
            <div className={styles.amountContainer}>Total Amount</div>
            <div className={styles.actionButtonsContainer}>Action Buttons</div>


        </div>
    );


}

export default Cart;
