import React from "react";

import styles from "./Cart.module.css"

const cartItems = [{
    id: 2, name: 'Schnitzel', description: 'A german specialty!', price: 16.5, amount: 2,
}].map(item => {
    return (
        <li key={item.id}>
            <h5 className={styles.mealName}>{item.name}</h5>
            <h6 className={styles.mealPrice}>${item.price.toFixed(2)} <div>x{item.amount}</div></h6>
        </li>

    );
})

const Cart = () => {
    return (
        <div className={styles.cartContainer}>
            <div className={styles.cartItemsContainer}><ul>{cartItems}</ul></div>
            <div className={styles.amountContainer}>Total Amount</div>
            <div className={styles.actionButtonsContainer}>Action Buttons</div>


        </div>
    );


}

export default Cart;
